import * as React from 'react';
import { useState, useEffect } from 'react';

// Import react navigation
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

// Import firebase
import firebase from 'firebase';

// firebase config
const firebaseConfig = {
    apiKey: "AIzaSyC9WVlZosV9ygptBGcvY9H6MxbuI2EaGx8",
    authDomain: "phytoflex-3f53f.firebaseapp.com",
    projectId: "phytoflex-3f53f",
    storageBucket: "phytoflex-3f53f.appspot.com",
    messagingSenderId: "437461344883",
    appId: "1:437461344883:web:5388696aaa0445c758c006"
};
if (firebase.apps.length == 0) {
    firebase.initializeApp(firebaseConfig);
}

// import Screens
import LoginScreen from './screens/landing/login';
import SignUpScreen from './screens/landing/signup';
import Forum from './screens/forum';
import { NavigationActions } from 'react-navigation';

// Login Screen
function loginScreen({navigation}){
    const gotoForum = () => {
        navigation.navigate('Forum');
    }
    
    const gotoSignUp = () => {
        navigation.navigate('SignUpScreen');
    }

    return(
        <LoginScreen
            gotoForum={gotoForum} 
            gotoSignUp={gotoSignUp}
            firebaseConfig={firebaseConfig}/>
    );
}

// Sign Up Screens
function signUpScreen({navigation}) {
    const gotoForum = () => {
        navigation.navigate('Forum');
    }

    return(
        <SignUpScreen
            gotoForum={gotoForum} 
            firebaseConfig={firebaseConfig}/>
    );
}
    
// Forum Screen
function forumScreen() {
    return (
        <Forum/>
    );
}

const Stack = createNativeStackNavigator();

function App() {
    
    const [userIsSignedIn, setuserIsSignedIn] = useState(false);

    firebase.auth().onAuthStateChanged((user) => {
        if (!user) {
            setuserIsSignedIn(false);
        } else {
            setuserIsSignedIn(true);
        }
    })

    if (!userIsSignedIn) {
        return (
            <NavigationContainer>
                <Stack.Navigator>
                    <Stack.Screen 
                        options={{headerShown: false}} 
                        name="Phytoflex" 
                        component={loginScreen} />
                    <Stack.Screen 
                        name="Forum" 
                        component={forumScreen} />
                    <Stack.Screen 
                        name="Sign Up" 
                        component={signUpScreen} />
                </Stack.Navigator>
            </NavigationContainer>
        );
    } else {
        return (
            <NavigationContainer>
                <Stack.Navigator>
                    <Stack.Screen 
                        options={{headerShown: false}} 
                        name="Phytoflex" 
                        component={forumScreen} />
                    <Stack.Screen 
                        name="Forum" 
                        component={forumScreen} />
                        <Stack.Screen 
                            name="Sign Up" 
                            component={signUpScreen} />
                </Stack.Navigator>
            </NavigationContainer>
        );
    }
}
        
export default App;
